<script lang="ts">
import {defineComponent} from "vue";

export default defineComponent({
    name: 'NewMapWindow',
    data() {
        return {
            mapData: {
                name: "" as string,
                resX: 1280 as number,
                resY: 720 as number,
            },
            res: "hd" as string,
            orient: "hor" as string
        }
    },
    methods: {
        createCanvas() {
					this.$store.dispatch('mainState/addNewMap',{title:this.mapData.name, resolution:(this.mapData.resX + "x" + this.mapData.resY)})
					this.$store.commit('modalFlags/setShowNewMapWin',false)
        }
    },
    watch: {
        res(val) {
            switch (val) {
                case "hd":
                    this.mapData.resX = 1280
                    this.mapData.resY = 720
                    break
                case "fhd":
                    this.mapData.resX = 1920
                    this.mapData.resY = 1080
                    break
                case "qhd":
                    this.mapData.resX = 2560
                    this.mapData.resY = 1440
                    break
                case "uhd":
                    this.mapData.resX = 3840
                    this.mapData.resY = 2160
            }
            let temp:number = 0
            if (this.orient == "vert" && this.mapData.resX > this.mapData.resY) {
                temp = this.mapData.resX
                this.mapData.resX = this.mapData.resY
                this.mapData.resY = temp
            }
        },
        orient() {
            let temp:number = 0
            temp = this.mapData.resX
            this.mapData.resX = this.mapData.resY
            this.mapData.resY = temp
        },
    }
})
</script>
